    return(

        <>
        <form>
            <input 
            onchange={handleInoutChange}
            placeholder="crie uma nova lista"
            value={list}
            type="text"
            />
            <button type="submit">list</button>
        </form>

        <table>
            <thead>
                <tr>
                    <th> list </th>
                </tr>
            </thead>

            <tbody> </tbody>
        </table>
        </>
    )
}

export default Todo;
