import React, {useState}from 'react';


const Todo = () => {
    const [list ,setList ]=useState('')
    const [newlist , setNewlist]=useState([])

    const handleInoutChange = event => setList(event.target.value)


    // handleFormSubmit essa função é para ele não atualizar a tela ao cliclar no botão

    const handleFormSubmit  = event => {
        event.preventDefault()


    }
    return(

        <>
        <form onSubmit ={handleFormSubmit}>
            <input 
                onchange={handleInoutChange}
                placeholder="crie uma nova lista"
                value={list}
                type="text"
            />
            <button type="submit">Add list</button>

        </form>

        <table>
            <thead>
                <tr>
                    <th> list </th>
                </tr>
            </thead>

            <tbody> </tbody>
        </table>
        </>
    )
}

export default Todo;
