import React, {useState}from 'react';


const Todo = () => {
    const [list ,setList ]=useState(null)
    const [newlist , setNewlist]=useState([])

    const handleInputChange = event => setList(event.target.value)


    // handleFormSubmit essa função é para ele não atualizar a tela ao cliclar no botão

    const handleFormSubmit  = event => {
        event.preventDefault()
        //Nessa linha abaixo ele tá pegando as novas listas colocando dentro de um array  e adicionando um nova lista
        setNewlist([ ... newlist, list])
        setList('')


    }
    return(

        <>
        <form onSubmit ={handleFormSubmit}>
            <input 
                onchange={handleInputChange}
                placeholder="crie uma nova lista"
                type="text"
                value={list}
            />
            <button type="submit">Add list</button>

        </form>

        <table>
            <thead>
                <tr>
                    <th> list </th>
                </tr>
            </thead>

            <tbody> </tbody>
        </table>
        </>
    )
}

export default Todo;
